/**
 * Upgrade plugin
 */
class Application_Plugin_Upgrade extends Zend_Controller_Plugin_Abstract
{
    /**
     * Register plugins by settings
     */
    public function dispatchLoopStartup(Zend_Controller_Request_Abstract $request)
    {
        $module = $request->getModuleName();

        if (!file_exists(APPLICATION_PATH . '/../conf/upgrading.php')) {
            return;
        }

        switch ($module) {
            case 'admin':
                $this->printMessage("Site is down for upgrade. Please initiate upgrade process.");
                break;

            default:
                $this->printMessage("The website you are trying to view is currently down for maintenance.<br>Normal service will resume shortly.");
                break;
        }
    }

    /**
     * Print message
     *
     * @param string $message
     * @return void
     */
    private function printMessage($message)
    {
        header('Expires: Thu, 01 Jan 1970 00:00:00 GMT');
        header('Cache-Control: no-store, no-cache, must-revalidate');

        $year = _(date("Y"));
        $template = <<<EOF
<!doctype html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <title>Newscoop - upgrade message</title>

    <style type="text/css">
        body {
            background-color:#fafafa;
            font: 12px/20px Arial, Helvetica, sans-serif;
            color: #696969;
            padding-top: 70px;
        }
        .pageWrapper {
            width:460px;
            margin:0 auto;
            padding: 15px;
            overflow: hidden;
        }
        .content {
            background: #f8f8f8;
            background: -webkit-linear-gradient(#f8f8f8 0%, #f2f2f2 100%);
            background: -moz-linear-gradient(#f8f8f8 0%, #f2f2f2 100%);
            background: -o-linear-gradient(#f8f8f8 0%, #f2f2f2 100%);
            background: linear-gradient(#f8f8f8 0%, #f2f2f2 100%);
            border: 1px solid #e4e4e4;
            padding: 16px;
        }
        .footer {
            clear:both;
            padding: 5px 0 0;
            text-align: center;
            margin-top: 4px;
            font-size: 11px;
            color: #666;
            line-height: 16px;
        }
        .logo {
            text-align: center;
            margin: 10px 0 30px 0;
        }
        a { color: #007fb3; text-decoration:none; }
        a:hover { text-decoration:underline; }
        h2 { 
            background: #f6f6f6;
            background: -webkit-linear-gradient(#fafafa 0%, #f6f6f6 100%);
            background: -moz-linear-gradient(#fafafa 0%, #f6f6f6 100%);
            background: -o-linear-gradient(#fafafa 0%, #f6f6f6 100%);
            background: linear-gradient(#fafafa 0%, #f6f6f6 100%);
            border:1px solid #afafaf;
            border-top-color:#FFF;
            border-left-color:#e5e5e5;
            border-right-color:#e5e5e5;
            margin:0 0 10px 0;
            padding:8px 10px;
            font-size:15px;
            color:#444;
            text-align:left;
            -moz-box-shadow: 0 2px 2px rgba(0,0,0,.10);
            -webkit-box-shadow: 0 2px 2px rgba(0,0,0,.10);
            box-shadow: 0 2px 2px rgba(0,0,0,.10); 
        }
        p {
            margin: 0 0 10px 0;
        }
    </style>
</head>
<body>  
<div class="pageWrapper">
    <div class="content">
        <div class="logo"><img src="data:image/gif;base64," border="0" alt="Newscoop logo">
        </div>
        <h2>$message</h2>
    </div>
    <div class="footer">
        <a href="http://newscoop.sourcefabric.org/" target="_blank">
            Newscoop</a>, the open content management system for professional journalists.
         <br>
            ©&nbsp;$year&nbsp;<a href="http://www.sourcefabric.org" target="_blank">Sourcefabric z.ú.</a>&nbsp;Newscoop       is distributed under GNU GPL v.3    
    </div>
</div>
</body>
</html>
EOF;
        echo $template;
        exit;
    }
}
